    @staticmethod
    def shuffle(articles: list[ArticleModel]):
        shuffled_list = []
        while articles:
            random_article = random.randint(0, len(articles) - 1)
            popped_article = articles.pop(random_article)
            shuffled_list.append(popped_article)
        return shuffled_list
